"use client";

import { CloudArrowUpIcon } from "@heroicons/react/24/outline";
import { User } from "lucide-react";
import React, { forwardRef, useRef } from "react";

import { AnimatedBeam } from "~/components/ui/motion/animated-beam";
import { useColorMode } from "~/lib/contexts/color-mode-context";
import { cn } from "~/lib/utils/cn";

const Circle = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "z-10 flex size-16 items-center justify-center rounded-full border-2 border-background-100 dark:border-background-600 bg-background-100 dark:bg-background-800 p-3",
        className,
      )}
    >
      {children}
    </div>
  );
});

Circle.displayName = "Circle";

export default function Deploy({ className }: { className?: string }) {
  const { colorMode } = useColorMode();

  const containerRef = useRef<HTMLDivElement>(null);
  const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div5Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  return (
    <div
      className={cn(
        "relative flex h-[300px] items-center justify-start overflow-hidden bg-background-100 dark:bg-background-800 rounded-xl p-4 bg-grainy max-w-lg w-full",
        className,
      )}
      ref={containerRef}
    >
      <div className="flex size-full max-w-lg flex-row justify-between gap-10">
        <div className="flex flex-col justify-center">
          <Circle ref={div7Ref}>
            <Icons.user />
          </Circle>
        </div>
        <div className="flex flex-col justify-center">
          <Circle ref={div6Ref} className="size-16">
            <Icons.deploy />
          </Circle>
        </div>
        <div className="flex flex-col justify-center gap-2">
          <Circle ref={div1Ref} className="size-16">
            <Icons.vercel colorMode={colorMode} />
          </Circle>
          <Circle ref={div2Ref}>
            <Icons.netlify />
          </Circle>
          <Circle ref={div3Ref}>
            <Icons.github colorMode={colorMode} />
          </Circle>
          <Circle ref={div4Ref}>
            <Icons.aws />
          </Circle>
        </div>
      </div>

      {/* AnimatedBeams */}
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div1Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div4Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        pathColor="#364C63"
        pathOpacity={1}
        gradientStartColor="#fcd34d"
        gradientStopColor="#f59e0b"
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div7Ref}
        duration={3}
      />
    </div>
  );
}

